import { confirmAlert } from 'react-confirm-alert'; // Import 
import './App.css';
import React, { useState ,useEffect } from 'react';
import './App.css';
import './bootstrap/dist/css/bootstrap.css'
import 'react-confirm-alert/src/react-confirm-alert.css'
import 'mdbreact/dist/css/mdb.css';
import {MDBRow,MDBCol, Button} from 'mdbreact';

const App = () => {
	const usersData = [ ]
	const initialFormState = { name: '', username: '', description: '', photo: [],count:0}

	const [ users, setUsers ] = useState(usersData)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)

  const addUser = user => {
		setUsers([ ...users, user ])
		user.photo = document.getElementById("output")
  }

	return (
		<div  className="container">
			<div className="flex-row">
				<div className="flex-large">
          <AddUserForm addUser={addUser} />
					<TodoApp items={this.state.items} />
				</div>
	 			<div className="flex-large">
          <UserTable users={users} setCurrentUser={setCurrentUser} currentUser={ currentUser} />

				</div>
			</div>
		</div>
	)
}
const UserTable = props => {
	const [ user, setUser ] = useState(props.currentUser)

  useEffect(
    () => {
      setUser(props.currentUser)
    },
    [ props ]
	)
	const handleInputChange = event => {
    const { name, value } = event.target

    setUser({ ...user, [name]: value })
	}
	const increment = ()=>{
		user.count += 1
		console.log(user.count )
	}
	return(
		<div >
			<div className="ff">

			{ (props.users.length >= 0) ? (
					props.users.map(user => (

						<div id = "carte" class="card card-cascade wider row ">
							<div class="view view-cascade overlay col-md-4">

								 <a href="#">
								 	<div class="mask rgba-white-slight"></div>
								 </a>
						 </div>

							 <div class="card-body card-body-cascade text-center">

								<h4 class="card-title"><strong>{user.name}</strong></h4>
								<h5 class="blue-text pb-2"><strong>{user.username}</strong></h5>
								<p class="card-text">{user.description}</p>
								<hr/>
								<a class="px-2 fa-lg li-ic"onClick={()=>document.getElementById("aff6").display="block"}>comment</a>	
								<a className="px-2 fa-lg li-ic"><i className="fas fa-heart" id="coeur"></i></a>	
      				</div>
					</div>
    )

					)				


				) : (
						console.log("1")
					)}
					</div>	
				{/* <div id="aff6" className="container">
			<form  onSubmit={(event)=>{
				event.preventDefault()
				document.getElementById("aff3").innerHTML=document.getElementById("in").value
			console.log(document.getElementById("aff3").innerHTML)
			}}>
			<button id="buton"onClick={() =>{
				 document.getElementById("aff").style.display="none"

				}


			 } className="button muted-button" onClick={()=>document.getElementById("aff6").display="none"}>
				x
				</button>
				<hr/>
				<input type="text" id="in" name="username"  onChange={handleInputChange}/>
				<button className="btn btn-success" id="buton" >comment</button><br></br>
				<ul>
				<li id="aff3"></li><br></br>
				</ul>

			</form></div> */}
			</div>		


		)
				}
const AddUserForm = props => {
  const initialFormState = {  name: '', username: '',description:'' ,photo:[],count:0}
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
	}

	const styl = {
			opacity: 0
	}
	const styl2 = {
		width: 30,

}
	return (
		<div >
		<div  className="container">
		<div  className="row">
		<div  className="col-md-5">
			<form onSubmit={event => {
      event.preventDefault()
      if (!user.name || !user.username || !user.description) return
			if(!isNaN(user.username)){
				props.addUser(user)
        //  props.user.photo.push(document.getElementById("output"))
				setUser(initialFormState)

			}




    }}>	
		<table  className="table">
			<thead >
				<tr>
					<th style={styl2} id="t"><label id="buton">Produit</label></th>
					<th ><input id="buton2" type="text" name="name" value={user.name} onChange={handleInputChange} /></th> 
				</tr>
				<tr>
					<th style={styl2} id="t" ><label id="buton">Prix</label></th>
					<th ><input id="buton" id="g"type="text" name="username" value={user.username} onChange={handleInputChange}  /></th> 
				</tr>
				<tr>
					<th style={styl2} id="t"><label id="buton">Description</label></th>
					<th ><textarea name="description" id="" cols="20" rows="2" value={user.description} onChange={handleInputChange}></textarea></th> 
				</tr>
			</thead>
			</table>
			<div>
			<div id="file" className="btn btn-dark">
			<label id="buton" for="photo">File</label>
			</div>
			<input  type="file" accept='image/*' name="photo" id="photo"  
			onChange={(event)=>{
				event.preventDefault();
				var input = event.target;
				var reader  = new FileReader();
				reader.onload = function (){
					var dataURL = reader.result;
					var output = document.getElementById("output");
					output.src=dataURL

					console.log(output)	
				}
				reader.readAsDataURL(input.files[0]);
			}}
			style={styl}/>
			</div>
			<br></br>
			<button class="btn btn-primary" id="buton1">Ajouter</button><br></br>
		</form>	

		</div>
		</div>
		</div>

		</div>
	)
}
class TodoApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = { items: [], text: '' };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <div>
        <h3>TODO</h3>
        <TodoList items={this.state.items} />
        <form onSubmit={this.handleSubmit}>
          <label htmlFor="new-todo">
            What needs to be done?
          </label>
          <input
            id="new-todo"
            onChange={this.handleChange}
            value={this.state.text}
          />
          <button>
            Add #{this.state.items.length + 1}
          </button>
        </form>
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (!this.state.text.length) {
      return;
    }
    const newItem = {
      text: this.state.text,
      id: Date.now()
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      text: ''
    }));
  }
}

class TodoList extends React.Component {
  render() {
    return (
      <ul>
        {this.props.items.map(item => (
          <li key={item.id}>{item.text}</li>
        ))}
      </ul>
    );
  }
}

  export default App;
