import { confirmAlert } from 'react-confirm-alert'; // Import 
import './App.css';
import React, { useState ,useEffect } from 'react';
import './App.css';
import './bootstrap/dist/css/bootstrap.css'
import 'react-confirm-alert/src/react-confirm-alert.css'
import 'mdbreact/dist/css/mdb.css';
import {MDBRow,MDBCol, Button} from 'mdbreact';

const App = () => {
	const usersData = [ ]
	const initialFormState = { name: '', username: '', description: '', photo: [],count:0}

	const [ users, setUsers ] = useState(usersData)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)

  const addUser = user => {
		setUsers([ ...users, user ])
		user.photo = document.getElementById("output")
  }

	return (
		<div  className="container">
			<div className="flex-row">
				<div className="flex-large">
          <AddUserForm addUser={addUser} />
				</div>
	 			<div className="flex-large">
          <UserTable users={users} setCurrentUser={setCurrentUser} currentUser={ currentUser} />

				</div>
			</div>
		</div>
	)
}
const UserTable = props => {
	const [ user, setUser ] = useState(props.currentUser)

  useEffect(
    () => {
      setUser(props.currentUser)
    },
    [ props ]
	)
	const handleInputChange = event => {
    const { name, value } = event.target

    setUser({ ...user, [name]: value })
	}

	return(

		<div className="ff">

			{ (props.users.length >= 0) ? (
					props.users.map(user => (

						<div id = "carte" class="card card-cascade wider row ">
							<div class="view view-cascade overlay col-md-4">
								<div >{user.photo[0]}</div>
								 <a href="#">
								 	<div class="mask rgba-white-slight"></div>
								 </a>
						 </div>

							 <div class="card-body card-body-cascade text-center">

								<h4 class="card-title"><strong>{user.name}</strong></h4>
								<h5 class="blue-text pb-2"><strong>{user.username}</strong></h5>
								<p class="card-text">{user.description}</p>
								<hr/>
								<a class="px-2 fa-lg li-ic"><i class="fab fa-linkedin-in">j'aime</i></a>	


							 </div>

							</div>


						))
				) : (
						console.log("1")
					)}

				</div>		

		)
				}
const AddUserForm = props => {
  const initialFormState = {  name: '', username: '',description:'' ,photo:[],count:0}
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
	}

	const styl = {
			opacity: 0,
	}
	return (
		<div  className="container">
		<div  className="row">
		<div  className="col-md-3"></div>
		<div  className="col-md-6">
			<form onSubmit={event => {
      event.preventDefault()
      if (!user.name || !user.username || !user.description) return
			if(!isNaN(user.username)){
				props.addUser(user)
        //  props.user.photo.push(document.getElementById("output"))
				setUser(initialFormState)
				document.getElementById("aff2").innerHTML=" "
			}

			else {
				document.getElementById("aff2").innerHTML="entrer un nombre"
			}


    }}>	

			<label id="buton">Produit</label>
			<input id="buton" type="text" name="name" value={user.name} onChange={handleInputChange} /><br></br>
			<label id="buton">Prix</label>
			<input id="buton" id="g"type="text" name="username" value={user.username} onChange={handleInputChange}  /><br></br>
			<label for="description" id="description">Description</label>
			<textarea name="description" id="" cols="20" rows="10" value={user.description} onChange={handleInputChange}></textarea><br></br>
			<div>
			<div id="file" className="btn btn-dark">
			<label id="buton" for="photo">File</label>
			</div>
			<input  type="file" accept='image/*' name="photo" id="photo"  
			onChange={(event)=>{
				event.preventDefault();
				var input = event.target;
				var reader  = new FileReader();
				reader.onload = function (){
					var dataURL = reader.result;
					var output = document.getElementById("output");
					output.src=dataURL

					console.log(output)	
				}
				reader.readAsDataURL(input.files[0]);
			}}
			style={styl}/>
			</div>

			<br></br>
			<button class="btn btn-primary" id="buton1">Ajouter</button><br></br>

			<span id="aff2" className=" text text-danger"></span>

		</form>	
		<img id='output'/>

		</div>

		<div  className="col-md-3"></div>
		</div>
		</div>
	)
}
const increment = props =>{

	return(
<button onClick={()=>{
												props.user.count += 1
											console.log(props.user.count )
	}}><a class="px-2 fa-lg tw-ic" ><i class="fab fa-twitter" >comment </i></a></button>
	)
}

  export default App;
