import React, { useState  } from 'react';
const Incr = ()=>{
    setUser({ ...user, [name]: value })

    const [count, setCount] = useState(0);
	const increment = ()=>{
		setCount(count+1)

    }
    return(
        <div class="mask rgba-white-slight">

        <a href="#"> <a class="px-2 fa-lg li-ic"onClick={()=>confirmAlert({
            customUI: ({onClose}) => {
                return (
                    <form id='ID'>
                    <div class="sss">
                    <div className="fa">
                    <button className="btn btn-danger naf" onClick={(b) => {
                        onClose()}
            } >X</button>
                    </div>
                        <input name='input'  id="e" placeholder={user.username} className="modifier" />

                        <button onClick={(e) => {
                            var teste=document.forms['ID'].elements['input'].value;


								e.preventDefault()
                                document.getElementById("sss").innerHTML+=teste ;
                                document.getElementById("sss").innerHTML+="<br>"
								coun.push(document.getElementById("e").value);
								console.log(coun)
								// for(var i=0;i<coun.length;i++){
								// 	document.getElementById("sss").innerHTML+=coun[i] ;
								// }
                                document.getElementById("e").value=' ';



            }} class="btn btn-primary d">comment</button>

        <p id="sss" class="r"></p>
                    </div>
                    </form>
                )

            }

        })
    }>comment</a><p onClick={increment}>like{count}</p>	
        </a>
        </div>

    )

}
export default Incr;
