import React from 'react';
const Incr = ()=>{
    const [count, setCount] = useState(0);
	const increment = ()=>{
		setCount(count+1)

    }
    return(
        <div class="mask rgba-white-slight">
        <a href="#"><p onClick={increment}>like</p><img src="a.jpg"  alt=""/>	
        </a><p id="count">{count}</p>
        </div>

    )

}
export default Incr;
