import { confirmAlert } from 'react-confirm-alert'; // Import 
import './App.css';
import React, { useState ,useEffect } from 'react';
import './App.css';
import './bootstrap/dist/css/bootstrap.css'
import 'react-confirm-alert/src/react-confirm-alert.css'
import 'mdbreact/dist/css/mdb.css';

const App = () => {
	const usersData = [ ]
	const initialFormState = { name: '', username: '', description: '', photo: '' }

	const [ users, setUsers ] = useState(usersData)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)

  const addUser = user => {
		setUsers([ ...users, user ])
  }

	return (
		<div  className="container">
			<div className="flex-row">
				<div className="flex-large">
          <AddUserForm addUser={addUser} />
				</div>
	 			<div className="flex-large">
          <UserTable users={users} setCurrentUser={setCurrentUser} currentUser={ currentUser} />

				</div>
			</div>
		</div>
	)
}
const UserTable = props => {
	const [ user, setUser ] = useState(props.currentUser)

  useEffect(
    () => {
      setUser(props.currentUser)
    },
    [ props ]
	)
	const handleInputChange = event => {
    const { name, value } = event.target

    setUser({ ...user, [name]: value })
	}

	return(
		<div>
			{ (props.users.length >= 0) ? (
					props.users.map(user => (
						<div class="card card-cascade wider">
							<div class="view view-cascade overlay">
								<img  class="card-img-top" id="output" alt="Card image cap"/>
								<a href="#">
									<div class="mask rgba-white-slight"></div>
								</a>
							</div>

							<div class="card-body card-body-cascade text-center">

								<h4 class="card-title"><strong>{user.name}</strong></h4>
								<h5 class="blue-text pb-2"><strong>{user.username}</strong></h5>
								<p class="card-text">{user.description}</p>

								<a class="px-2 fa-lg li-ic"><i class="fab fa-linkedin-in"></i></a>
								<a class="px-2 fa-lg tw-ic"><i class="fab fa-twitter"></i></a>
								<a class="px-2 fa-lg fb-ic"><i class="fab fa-facebook-f"></i></a>

							</div>
							</div>
						))
				) : (
						console.log("1")
					)}
		</div>
		)
				}

const AddUserForm = props => {
  const initialFormState = {  name: '', username: '',description:'' }
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
	}

	return (
		<div>
		{ (props.users.length >= 0) ? (
					props.users.map(user => (
						<div class="card card-cascade wider">
							<div class="view view-cascade overlay">
								<img  class="card-img-top" id="output" alt="Card image cap"/>
								<a href="#">
									<div class="mask rgba-white-slight"></div>
								</a>
							</div>

							<div class="card-body card-body-cascade text-center">

								<h4 class="card-title"><strong>{user.name}</strong></h4>
								<h5 class="blue-text pb-2"><strong>{user.username}</strong></h5>
								<p class="card-text">{user.description}</p>

								<a class="px-2 fa-lg li-ic"><i class="fab fa-linkedin-in"></i></a>
								<a class="px-2 fa-lg tw-ic"><i class="fab fa-twitter"></i></a>
								<a class="px-2 fa-lg fb-ic"><i class="fab fa-facebook-f"></i></a>

							</div>
							</div>
						))
				) : (
						console.log("1")
					)}
			<form onSubmit={event => {
      event.preventDefault()
      if (!user.name || !user.username || !user.description) return
			if(!isNaN(user.username)){
				props.addUser(user)
				setUser(initialFormState)
				document.getElementById("aff2").innerHTML=" "
			}

			else {
				document.getElementById("aff2").innerHTML="entrer un nombre"
			}


    }}>	
			<table  className="table">
			<tr key={user.id}>
			<td scope="col"><label id="buton">Produit</label></td>
			<td scope="col"><input id="buton" type="text" name="name" value={user.name} onChange={handleInputChange} /></td>
			<td scope="col"><label id="buton">Prix</label></td>
			<td scope="col"><input id="buton" id="g"type="text" name="username" value={user.username} onChange={handleInputChange}  /></td>
			<td scope="col"><label id="buton">Description</label></td>
			<td scope="col"><input id="buton" type="text" name="description" value={user.description} onChange={handleInputChange} /></td>
			<td scope="col"><label id="buton" for="photo">File</label></td>
			<td scope="col"><input type="file" accept='image/*' name="photo" id="buton"  
			onChange={(event)=>{
				event.preventDefault();
				var input = event.target;
				var reader  = new FileReader();
				reader.onload = function (){
					var dataURL = reader.result;
					var output = document.getElementById("output");
					output.src=dataURL
					console.log(output)	
				}
				reader.readAsDataURL(input.files[0]);
			}}
			  /></td>
			<td scope="col"><button class="btn btn-success" id="buton">Ajouter</button><br></br></td>
			</tr>
			</table>

			<span id="aff2" className=" text text-danger"></span>

		</form>
		<img id='output'/>
		</div>

	)
}

  export default App;
