import { confirmAlert } from 'react-confirm-alert'; // Import 
import './App.css';
import React, { useState ,useEffect } from 'react';
import './App.css';
import './bootstrap/dist/css/bootstrap.css'
import 'react-confirm-alert/src/react-confirm-alert.css'
import {MDBRow,MDBCol} from 'mdbreact';
import UserTable from './usertable'

	const App = () => {
const usersData = [ ]
	const initialFormState = { name: '', username: '', description: '', photo: []}

	const [ users, setUsers ] = useState(usersData)
	const [count, setCount] = useState(0);
	const [ currentUser, setCurrentUser ] = useState(initialFormState)

  const addUser = user => {
		setUsers([ ...users, user ])
		user.photo = document.getElementById("output")
  }

	return (
		<div  className="container">
			<div className="flex-row">
				<div className="flex-large">
          <AddUserForm addUser={addUser} />
				</div>
	 			<div className="flex-large">
          <UserTable users={users} setCurrentUser={setCurrentUser} currentUser={ currentUser} setCount={setCount}
					count={count}/>

				</div>
			</div>
		</div>
	)
}

const AddUserForm = props => {
  const initialFormState = {  name: '', username: '',description:'' ,photo:[],count:0}
	const [ user, setUser ] = useState(initialFormState)

	const handleInputChange = event => {
		const { name, value } = event.target

		setUser({ ...user, [name]: value })
	}

	const styl = {
			opacity: 0
	}
	const styl2 = {
		width: 30,

}
	return (

		<div  className="container">
		<div  className="row">
		<div  className="col-md-5">
			<form onSubmit={event => {
      event.preventDefault()
      if (!user.name || !user.username || !user.description) return
			if(!isNaN(user.username)){
				props.addUser(user)
        //  props.user.photo.push(document.getElementById("output"))
				setUser(initialFormState)

			}




    }}>	
		<table  className="table">
			<thead >
				<tr>
					<th style={styl2} id="t"><label id="buton">Produit</label></th>
					<th ><input id="buton2" type="text" name="name" value={user.name} onChange={handleInputChange} /></th> 
				</tr>
				<tr>
					<th style={styl2} id="t" ><label id="buton">Prix</label></th>
					<th ><input id="buton" id="g"type="text" name="username" value={user.username} onChange={handleInputChange}  /></th> 
				</tr>
				<tr>
					<th style={styl2} id="t"><label id="buton">Description</label></th>
					<th ><textarea name="description" id="" cols="20" rows="2" value={user.description} onChange={handleInputChange}></textarea></th> 
				</tr>
			</thead>
			</table>
			<div>
			<div id="file" className="btn btn-dark">
			<label id="buton" for="photo">File</label>
			</div>
			<input  type="file" accept='image/*' name="photo" id="photo"  
			onChange={(event)=>{
				event.preventDefault();
				var input = event.target;
				var reader  = new FileReader();
				reader.onload = function (){
					var dataURL = reader.result;
					var output = document.getElementById("output");
					output.src=dataURL

					console.log(output)	
				}
				reader.readAsDataURL(input.files[0]);
			}}
			style={styl}/>
			</div>
			<br></br>
			<button class="btn btn-primary" id="buton1">Ajouter</button><br></br>
		</form>	

		</div>
		</div>
		</div>


	)
}




  export default App;
