import './glyphicons/glyphicons.js'
import React, { useState  } from 'react';
const Incr = ()=>{
    const [count, setCount] = useState(0);
	const increment = ()=>{
		setCount(count+1)

    }
    return(
        <div class="mask rgba-white-slight">
        < script src = " https://cdn.rawgit.com/download/glyphicons/0.1.0/glyphicons.js " > </ script >
        <a href="#"><p onClick={increment}>like{count}<span className="glyphicon glyphicon-heart-empty"></span></p>	
        </a>
        </div>

    )

}
export default Incr;
